import React, { useState } from "react";
import ButtonsContainer from "./components/ButtonsContainer";
import DisplayContainer from "./components/DisplayContainer";
import "./styles.css";

function App() {
  const [display, setDisplay] = useState(""); // For displaying input and result
  const [result, setResult] = useState(null); // To store the last calculated result
  const [isResultDisplayed, setIsResultDisplayed] = useState(false); // To track if result is shown

  // Date logic to get the current date
  const currentDate = new Date();
  const day = currentDate.getDate();
  const month = currentDate.toLocaleString('default', { month: 'long' });
  const year = currentDate.getFullYear();
  const formattedDate = `${day} ${month}, ${year}`; // Create formatted date string

  // Append clicked numbers or operators to the display
  function handleClick(e) {
    const targetValue = e.target.name;

    // If a result is already displayed, start a new expression
    if (isResultDisplayed) {
      setDisplay(targetValue);
      setIsResultDisplayed(false);
    } else {
      setDisplay(display + targetValue);
    }
  }

  function operatorClick(operator) {
    let lastCharacter = display.slice(-2);
    let operatorsArray = ["+ ", "- ", "* ", "/ "];

    if (display === "" || operatorsArray.includes(lastCharacter)) return;

    setDisplay((prevDisplay) => {
      return prevDisplay + " " + operator + " ";
    });
  }

  function handleEqual() {
    if (display.slice(-2).includes("+ ", "- ", "* ", "/ ")) return;

    try {
      const resultValue = calculate(display);
      setResult(resultValue);

      // Show the full expression with the result in one line and allow further calculations
      setDisplay(`${resultValue}`);
      setIsResultDisplayed(true); // Indicate that the result is displayed
    } catch (error) {
      setDisplay("Error");
    }
  }

  function calculate(expression) {
    const tokens = expression.split(" ");
    let resultValue = parseInt(tokens[0]);

    for (let i = 1; i < tokens.length; i += 2) {
      const operator = tokens[i];
      const nextNumber = parseInt(tokens[i + 1]);

      switch (operator) {
        case "+":
          resultValue += nextNumber;
          break;
        case "-":
          resultValue -= nextNumber;
          break;
        case "*":
          resultValue *= nextNumber;
          break;
        case "/":
          resultValue /= nextNumber;
          break;
        default:
          resultValue = "Error";
      }
    }
    return resultValue;
  }

  function clear() {
    setDisplay("");
    setResult(null);
    setIsResultDisplayed(false);
  }

  function backspace() {
    setDisplay(display.slice(0, -1));
  }

  return (
    <>
      <div className="container">
        <div className="calculator">
          {/* Only one display area for both input and result */}
          <DisplayContainer
            display={display} // Single display area
            backspace={backspace}
            clear={clear}
          />
          <ButtonsContainer
            operatorClick={operatorClick}
            handleClick={handleClick}
            handleEqual={handleEqual}
          />
          <div className="side-by-side">
            {/* Display the dynamically generated date */}
            <p className="text-white">{formattedDate}</p>
            <p className="text-white">Shreya Nandhivargam</p>
          </div>
        </div>
      </div>
    </>
  );
}

export default App;
